import React, {Component} from 'react'
import './Targets.css'

class Targets extends Component {
   constructor(props){
      super(props)
      this.state = {
         dailyTargets: 0,
         achieved: 0,
         remaining: 0
      }
   }

   componentDidMount() {
      this.setState({
         dailyTargets: this.props.items.length - 1,
         achieved: this.props.items.filter(x => x.done === true).length,
         remaining: this.state.dailyTargets - this.state.achieved
      })
   }

   componentWillUnmount() {
      this.setState({
         dailyTargets: 0,
         achieved: 0,
         remaining: 0
      })
   }

   render() {
      return(
         <div className='container targets'>
            <div className='container-header'>
               <h2>Daily Targets</h2>
            </div>
            <div className='container-data'>
               <h1>{this.state.dailyTargets}</h1>
               <h1>{this.state.achieved}</h1>
               <h1>{this.state.remaining}</h1>
            </div>
         </div>
      )
   }
}

export default Targets;
